import './App.css';
import { useEffect, useState } from 'react';
import {getGuests} from "./HotelService"
import HotelForm from './HotelForm';
import HotelGrid from './HotelGrid';

function App() {

  const [guests, setGuests] = useState([]);

  useEffect(() => {
    getGuests()
    .then((data) => {
      console.log(data);
      setGuests(data)
    })
  }, []);

  const addGuest = (guest) => {
    let temp = guests.map(g => g);
    temp.push(guest);
    setGuests(temp);
  }




  return (
    <div className="App">
      <HotelForm addGuest={addGuest} />
      <HotelGrid guests={guests} />
    </div>
  );
}

export default App;
